import { Router } from "express";
import { body } from "express-validator";
import { deleteUser, getUserById, getUsers, updateUser } from "../controllers/user.controller";
import { auth, isRole } from "../middlewares/auth";
const router = Router();
router.get("/", auth, isRole(["restaurant", "driver", "customer"]), getUsers);
router.get("/:id", auth, getUserById);
router.put("/:id", auth, [body("name").optional().isString()], updateUser);
router.delete("/:id", auth, isRole(["restaurant"]), deleteUser);
export default router;
